# ~~~~~~~~~ Part 1 ~~~~~~~~~
present_floor = 0

for cmd in puzzle_input:
    if cmd == "(":
        present_floor += 1
    else:
        present_floor -= 1

print("Santa has arrived at %d" % present_floor)

# ~~~~~~~~~ Part 2 ~~~~~~~~~
present_floor = 0
ticker = 0

for cmd in puzzle_input:
    ticker += 1
    if cmd == "(":
        present_floor += 1
    else:
        present_floor -= 1

    if present_floor == -1:
        print("The first time Santa arrives at floor -1 is at pos: %d" % ticker)
        break


